#Les questions
def questions
  puts "Hello, choisi le numéro qui correspond à la question de ton choix :"
  puts "• 1 • Combien y a t-il de handle dans cette array ?"
  puts "• 2 • Quel est le handle le plus court de cette liste ?"
  puts "• 3 • Combien y a t-il de handle contenant 5 caractères ?"
  puts "• 4 • Combien commencent par une majuscule ?"
  puts "• 5 • Pour trier la liste de handle par ordre alphabétique ."
  puts "• 6 • Pour trier les handle par taille (du plus petit nombre de caractère au plus grand) ."
  puts "• 7 • Quelle est la position dans l'array de la personne se nommant @epenser ?"
  puts "• 8 • Pour avoir le nombre de handle avec un certain nombre de caractère ."
  puts "• 0 • Pour sortir ."
  print "> "
  question = gets.chomp.to_i
  if question >= 0 && question <= 8
    return question
  else
    puts "• Ceci n'est pas un bon numéro, reesaye ! •"
    return (-1)
  end
end

#Combien de handle dans l'array?
def nombre(arr)
  puts "• Combien y a t-il de handle dans cette array ? •"
  puts arr.length
end

#Quel est le handle le plus court?
def court(arr)
  puts "• Quel est le handle le plus court de cette liste ? •"
  puts arr.min { |a, b| a.length <=> b.length}
end

#Combien de handle contenant 5 caractères?
def caractere(arr)
  puts "• Combien y a t-il de handle contenant 5 caractères ? •"
  num = 0
  arr.each do |i|
      if (i.length - 1) == 5
        num = num + 1
    end
  end
  puts num
end

#Combien commencent par une majuscule?
def majuscule(arr)
  puts "• Combien commencent par une majuscule ? •"
  letter_maj = arr.select {|word| word.start_with?(/@[[:upper:]]/)}
  puts letter_maj.length
end

#Trie la liste de handle par ordre alphabétique.
def alphabetique(arr)
  puts "• Voici la liste des handles par ordre alphabétique : •"
  alphab = arr.sort_by {|word| word.downcase}
  alphab.each do |i|
    puts i
  end
end

#Trie la liste de handle par taille des handles.
def taille(arr)
  puts "• Voici la liste des handles par taille : •"
  list = arr.sort_by {|letter| letter.length}
  list.each do |i|
    puts i
  end
end

#Quelle est la position dans l'array de la personne @epenser.
def epenser(arr)
  puts "• J'ai retrouvé @epenser, il se trouve en position : •"
  puts arr.index("@epenser")
end

#Sors moi une répartition des handle par taille de ces derniers.
def repartitions(arr)
  puts "• Voici le nombre de handle avec un certain nombre de caractère : •"
  x = 0
  length = arr.length
  y = 2
  while x != length
      z = 0
      arr.each do |i|
          if (i.length) == y
            z = z + 1
          end
      end
      puts "Il y a #{z} handle(s) de #{y - 1} caractère(s)"
      y = y + 1
      x = x + z
  end
end

#Les réponses aux questions
def reponse(question, arr)
  if question == 1
    nombre(arr)
  elsif question == 2
    court(arr)
  elsif question == 3
    caractere(arr)
  elsif question == 4
    majuscule(arr)
  elsif question == 5
    alphabetique(arr)
  elsif question == 6
    taille(arr)
  elsif question == 7
    epenser(arr)
  elsif question == 8
    repartitions(arr)
  end
  puts ""
end

def go
  arr = handle
  while ((question = questions) !=0)
    reponse(question, arr)
  end
  puts ""
  puts "• À bientôt . MERCI ! •"
end

go
